import React, { useState, useEffect } from "react";
import axios from "axios";
import "./.css"

function Dashboard() {
    const [documents, setDocuments] = useState([]);
    const [file, setFile] = useState(null);
    const token = localStorage.getItem("token");

    useEffect(() => {
        if (token) {
            axios.get("http://localhost:3000/documents", { headers: { Authorization: token } })
                .then(res => setDocuments(res.data))
                .catch(err => console.error(err));
        }
    }, [token]);

    const handleUpload = async () => {
        if (!file) return;
        const formData = new FormData();
        formData.append("file", file);
        await axios.post("http://localhost:3001/upload", formData, { headers: { Authorization: token } });
        window.location.reload();
    };

    return (
        <div className="p-6">
            <h2 className="text-2xl font-bold">Dashboard</h2>
            <input type="file" className="border p-2 mt-4" onChange={(e) => setFile(e.target.files[0])} />
            <button className="bg-blue-500 text-white p-2 mt-2" onClick={handleUpload}>Upload</button>
            <ul className="mt-4">
                {documents.map(doc => (
                    <li key={doc._id}>{doc.filename}</li>
                ))}
            </ul>
        </div>
    );
}

export default Dashboard;
