import React, { useState } from "react";
import axios from "axios";

function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const handleSignup = async () => {
        try {
            await axios.post("http://localhost:3001/signup", { username, password });
            alert("Signup successful. You can now log in.");
        } catch (error) {
            console.error("Signup failed", error);
        }
    };

    return (
        <div className="p-6 text-center">
            <h2 className="text-2xl font-bold">Signup</h2>
            <input type="text" className="border p-2 mt-4" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
            <input type="password" className="border p-2 mt-2" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <button className="bg-blue-500 text-white p-2 mt-2" onClick={handleSignup}>Signup</button>
        </div>
    );
}

export default Signup;
