import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    const handleLogin = async () => {
        try {
            const res = await axios.post("http://localhost:3001/login", { username, password });
            localStorage.setItem("token", res.data.token);
            navigate("/dashboard");
        } catch (error) {
            console.error("Login failed", error);
        }
    };

    return (
        <div className="p-6 text-center">
            <h2 className="text-2xl font-bold">Login</h2>
            <input type="text" className="border p-2 mt-4" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
            <input type="password" className="border p-2 mt-2" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <button className="bg-blue-500 text-white p-2 mt-2" onClick={handleLogin}>Login</button>
        </div>
    );
}

export default Login;
